package com.example.instagramclone

import android.app.Activity
import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.View
import android.widget.Button
import android.widget.EditText
import android.widget.TextView
import android.widget.Toast
import com.example.instagramclone.model.Student

class Login : AppCompatActivity(),View.OnClickListener {

    private lateinit var etCoventryId: EditText
    private lateinit var etPassword: EditText
    private lateinit var btnLogin: Button
    private lateinit var tvGoToReg: TextView
    private var studentArrayList=ArrayList<Student>()
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_login)

        etCoventryId=findViewById(R.id.etLoginCuid)
        etPassword=findViewById(R.id.etLoginPassword)
        btnLogin=findViewById(R.id.btnLogin)
        tvGoToReg=findViewById(R.id.tvGotoReg)
        studentArrayList.add(Student(1,"Anup","Shahi","Anoop","anup123","24A","data:image/jpeg;base64,"))
        studentArrayList.add(Student(2,"Bishal","Sai","Bsal","bsal123","25A","data:image/jpeg;base64,"))
        studentArrayList.add(Student(3,"Chris","Martin","Chris","chris123","26A","data:image/jpeg;base64,"))

        btnLogin.setOnClickListener(this)
        tvGoToReg.setOnClickListener(this)


    }

    override fun onClick(v: View?) {
        when(v?.id){
            R.id.tvGotoReg->{
                val intent= Intent(this, Register::class.java)
                startActivityForResult(intent,1)
            }

            R.id.btnLogin -> {
                if (isValid()) {
                    if (userValidation()) {
                        val intent = Intent(this, MainActivity::class.java)
                        intent.putExtra("studentArrayList", studentArrayList)
                        startActivity(intent)
                    } else {
                        Toast.makeText(this, "Invalid Username / password", Toast.LENGTH_LONG).show()
                    }
                }
            }
        }
    }
    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {
        super.onActivityResult(requestCode, resultCode, data)
        if(requestCode==1){
            if(resultCode== Activity.RESULT_OK){
                val students=data?.getParcelableExtra<Student>("students")
                studentArrayList.add(students!!)
            }
        }
    }

    private fun isValid():Boolean{
        when{
            etCoventryId.text?.isEmpty() == true ->{
                etCoventryId.error = "Field must not be empty"
                return false
            }
            etPassword.text?.isEmpty() == true ->{
                etPassword.error = "Field must not be empty"
                return false
            }
        }
        return true
    }

    fun userValidation(): Boolean{
        for (i in studentArrayList.indices){
            if (etCoventryId.text.toString().toInt() == studentArrayList[i].cuID && etPassword.text.toString() == studentArrayList[i].password){
                return true
            }
        }
        return false
    }
}